// couch/waitlist.js
//
module.exports = function (couch) {
	var database = couch.db;
	
	var addRequest = function (request, callback) {
		var doc = {
			circle: request.circle,
			email: request.email,
			things: request.things,
			type: "waitlist",
			timestamp: Date.now()
		};

		database.insert(doc, callback);
	};

	var getWaitlist = function (callback) {
		couch.view("admin/waitlist", callback);
	};

	return {
		add: addRequest,
		get: getWaitlist
	};
};